export const extractArgumentsFromConfigs = (configs) => {
    if (!configs)
        return {};
    return Object.fromEntries(Object.entries(configs)
        .filter(([_name, def]) => def.argument)
        .map(([name, def]) => [
        name,
        {
            description: def.description,
            ...def.argument,
        },
    ]));
};
